import { RandomIvGen } from "../iv/mod_node.js";
import { AesCommon } from "./aes-common_node.js";
/**
 * AES-CBC encryption algorithm.
 *
 * Initialization Vectors must be 16 octets.
 * During encryption, if IV is unspecified, it is randomly generated.
 * During decryption, quality of IV is not checked.
 */
export const AESCBC = new (class extends AesCommon {
    name = "AES-CBC";
    uuid = "a3840ac4-b29d-4ab5-a255-2894ec254223";
    ivLength = 16;
    getIvGen() {
        return new RandomIvGen(this.ivLength);
    }
    allowAdditionalData = false;
    tagSize = 0;
    defaultInfo = {};
})();
