import { assert } from "@ndn/util";
import { ECDSA } from "../algo/mod_node.js";
import { generateKeyInternal } from "./impl-generate_node.js";
import { createSigner } from "./signing-signer_node.js";
import { createVerifier } from "./signing-verifier_node.js";
import { CryptoAlgorithm } from "./types_node.js";
export async function generateSigningKey(...a) {
    const [keyName, algo, gen] = await generateKeyInternal(ECDSA, a);
    assert(CryptoAlgorithm.isSigning(algo));
    return [
        createSigner(keyName, algo, gen),
        createVerifier(keyName, algo, gen),
    ];
}
